 import { useGetUsersQuery } from './usersApiSlice'
 import User from './User'

 const UsersList = () => {
  const {
    data: users,
    isLoading,
    isSuccess,
    isError,
    error
  } = useGetUsersQuery(null, {
    pollingInterval: 60000,
    refetchOnFocus: true,
    refetchOnMountOrArgChange: true
  })

  let content

  if (isLoading) content = <p>Loading...</p>

  if(isError) {
    content = <p className="errmsg">{error?.data?.message}</p>
  }


  if (isSuccess) {
    const { ids } = users

    const tableContent = ids?.length ? ids.map(userId => <User key={userId} userId={userId} />) : null

    content = (
      <table className="table table--users">
        <thead className="table__thead">
          <tr>
            <th scope="col" className="table__thuser__username">Username</th>
            <th scope="col" className="table__thuser__roles">Roles</th>
            <th scope="col" className="table__thuser__edit">Edit</th>
          </tr>
        </thead>
        <tbody>
          {tableContent}
        </tbody>
      </table>
    )

  }

  return content
}

export default UsersList
