import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faPenToSquare } from "@fortawesome/free-solid-svg-icons"
import { useNavigate } from "react-router-dom"

import { useSelector } from "react-redux"
import { selectVideoById } from "./videosApiSlice"


const Video = ({ videoId }) => {
    const video = useSelector(state => selectVideoById(state, videoId))
    const navigate = useNavigate()

    if (video) {
        const created = new Date(note.createdAt).toLocaleString('en-US', { day: 'numeric', month: 'long' })
        const updated = new Date(note.updatedAt).toLocaleString('en-US', { day: 'numeric', month: 'long' })
        const handleEdit = () => navigate(`/dash/videos/${videoId}`)

        const videoRolesString = video.roles.toString().replaceAll(',', ', ') // 'admin,video' => 'admin, video'

        const cellStatus = video.active ? '' : 'table__cell--inactive'

        return (
            <tr className="table__row video">
                <td className={`table__cell ${cellStatus}`}>{video.videoname}</td>
                <td className={`table__cell ${cellStatus}`}>{videoRolesString}</td>
                <td className={`table__cell ${cellStatus}`}>
                    <button className="icon-button table__button" onClick={handleEdit}>
                        <FontAwesomeIcon icon={faPenToSquare} />
                    </button>
                </td>
            </tr>
        )
    } else return null

}

export default Video
